package cn.xgame.config.gen;import java.util.List;import java.util.Map;import x.javaplus.collections.Lists;import x.javaplus.csv.util.Csv;import cn.xgame.a.system.SystemCfg;import cn.xgame.config.o.Bbuilding;
import cn.xgame.config.o.Captain;
import cn.xgame.config.o.Ectype;
import cn.xgame.config.o.Ectypelist;
import cn.xgame.config.o.Enemy;
import cn.xgame.config.o.Item;
import cn.xgame.config.o.Notice;
