public class CsvGen {	public static final List<Bbuilding> bbuildings = Lists.newArrayList();	public static final List<Captain> captains = Lists.newArrayList();	public static final List<Ectype> ectypes = Lists.newArrayList();	public static final List<Ectypelist> ectypelists = Lists.newArrayList();	public static final List<Enemy> enemys = Lists.newArrayList();	public static final List<Item> items = Lists.newArrayList();	public static final List<Notice> notices = Lists.newArrayList();	public static final List<PlayerInit> playerinits = Lists.newArrayList();	public static final List<Sbuilding> sbuildings = Lists.newArrayList();	public static final List<Ship> ships = Lists.newArrayList();	public static final List<Stars> starss = Lists.newArrayList();	public static final List<Tech> techs = Lists.newArrayList();	public static final List<Treasure> treasures = Lists.newArrayList();	public static final List<Weapon> weapons = Lists.newArrayList();
	public static void load(){		loadBbuilding( "bbuilding.csv" );		loadCaptain( "captain.csv" );		loadEctype( "ectype.csv" );		loadEctypelist( "ectypelist.csv" );		loadEnemy( "enemy.csv" );		loadItem( "item.csv" );		loadNotice( "notice.csv" );		loadPlayerInit( "playerInit.csv" );		loadSbuilding( "sbuilding.csv" );		loadShip( "ship.csv" );		loadStars( "stars.csv" );		loadTech( "tech.csv" );		loadTreasure( "treasure.csv" );		loadWeapon( "weapon.csv" );
	}	private static void loadBbuilding( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Bbuilding o = new Bbuilding(data);			bbuildings.add( o );		}	}	private static void loadCaptain( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Captain o = new Captain(data);			captains.add( o );		}	}	private static void loadEctype( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Ectype o = new Ectype(data);			ectypes.add( o );		}	}	private static void loadEctypelist( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Ectypelist o = new Ectypelist(data);			ectypelists.add( o );		}	}	private static void loadEnemy( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Enemy o = new Enemy(data);			enemys.add( o );		}	}	private static void loadItem( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Item o = new Item(data);			items.add( o );		}	}	private static void loadNotice( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Notice o = new Notice(data);			notices.add( o );		}	}	private static void loadPlayerInit( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			PlayerInit o = new PlayerInit(data);			playerinits.add( o );		}	}	private static void loadSbuilding( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Sbuilding o = new Sbuilding(data);			sbuildings.add( o );		}	}	private static void loadShip( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Ship o = new Ship(data);			ships.add( o );		}	}	private static void loadStars( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Stars o = new Stars(data);			starss.add( o );		}	}	private static void loadTech( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Tech o = new Tech(data);			techs.add( o );		}	}	private static void loadTreasure( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Treasure o = new Treasure(data);			treasures.add( o );		}	}	private static void loadWeapon( String file ){				Csv csv = new Csv( SystemCfg.FILE_NAME + "configs/" + file );				for( Map<String, String> data : csv.getValues() ){			Weapon o = new Weapon(data);			weapons.add( o );		}	}
	public static Bbuilding getBbuilding( int x ){		for( Bbuilding o : bbuildings ){			if( o.id == x )				return o;		}		return null;	}	public static Captain getCaptain( int x ){		for( Captain o : captains ){			if( o.id == x )				return o;		}		return null;	}	public static Ectype getEctype( int x ){		for( Ectype o : ectypes ){			if( o.id == x )				return o;		}		return null;	}	public static Ectypelist getEctypelist( int x ){		for( Ectypelist o : ectypelists ){			if( o.id == x )				return o;		}		return null;	}	public static Enemy getEnemy( int x ){		for( Enemy o : enemys ){			if( o.id == x )				return o;		}		return null;	}	public static Item getItem( int x ){		for( Item o : items ){			if( o.id == x )				return o;		}		return null;	}	public static Notice getNotice( int x ){		for( Notice o : notices ){			if( o.id == x )				return o;		}		return null;	}	public static PlayerInit getPlayerInit( int x ){		for( PlayerInit o : playerinits ){			if( o.currency == x )				return o;		}		return null;	}	public static Sbuilding getSbuilding( int x ){		for( Sbuilding o : sbuildings ){			if( o.id == x )				return o;		}		return null;	}	public static Ship getShip( int x ){		for( Ship o : ships ){			if( o.id == x )				return o;		}		return null;	}	public static Stars getStars( int x ){		for( Stars o : starss ){			if( o.id == x )				return o;		}		return null;	}	public static Tech getTech( int x ){		for( Tech o : techs ){			if( o.id == x )				return o;		}		return null;	}	public static Treasure getTreasure( int x ){		for( Treasure o : treasures ){			if( o.id == x )				return o;		}		return null;	}	public static Weapon getWeapon( int x ){		for( Weapon o : weapons ){			if( o.id == x )				return o;		}		return null;	}
}