package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class AskingPo {	public final int id;	public final String name;	public final String des;	public final String icon;	public final int heavy;	public final byte settype;	public final byte type;	public final int intvalue;	public final float pctvalue;

	public AskingPo( AskingPo clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.icon = clone.icon;		this.heavy = clone.heavy;		this.settype = clone.settype;		this.type = clone.type;		this.intvalue = clone.intvalue;		this.pctvalue = clone.pctvalue;

	}	public AskingPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		icon = data.get("icon");		heavy = Integer.parseInt( StringUtil.convertNumberString( data.get("heavy") ) );		settype = Byte.parseByte( StringUtil.convertNumberString( data.get("settype") ) );		type = Byte.parseByte( StringUtil.convertNumberString( data.get("type") ) );		intvalue = Integer.parseInt( StringUtil.convertNumberString( data.get("intvalue") ) );		pctvalue = Float.parseFloat( StringUtil.convertNumberString( data.get("pctvalue") ) );

	}}