package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class BbuildingPo {	public final int id;	public final String name;	public final String intro;	public final String des;	public final String icon;	public final String model;	public final String needres;	public final String iconbg;	public final int needtime;	public final short type;	public final byte usegrid;	public final int ProduceValue;	public final String ProduceTpye;	public final int ram;	public final byte buildtype;	public final byte removedtype;	public final String mutex;	public final String front;	public final String technology;

	public BbuildingPo( BbuildingPo clone ){		this.id = clone.id;		this.name = clone.name;		this.intro = clone.intro;		this.des = clone.des;		this.icon = clone.icon;		this.model = clone.model;		this.needres = clone.needres;		this.iconbg = clone.iconbg;		this.needtime = clone.needtime;		this.type = clone.type;		this.usegrid = clone.usegrid;		this.ProduceValue = clone.ProduceValue;		this.ProduceTpye = clone.ProduceTpye;		this.ram = clone.ram;		this.buildtype = clone.buildtype;		this.removedtype = clone.removedtype;		this.mutex = clone.mutex;		this.front = clone.front;		this.technology = clone.technology;

	}	public BbuildingPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		intro = data.get("intro");		des = data.get("des");		icon = data.get("icon");		model = data.get("model");		needres = data.get("needres");		iconbg = data.get("iconbg");		needtime = Integer.parseInt( StringUtil.convertNumberString( data.get("needtime") ) );		type = Short.parseShort( StringUtil.convertNumberString( data.get("type") ) );		usegrid = Byte.parseByte( StringUtil.convertNumberString( data.get("usegrid") ) );		ProduceValue = Integer.parseInt( StringUtil.convertNumberString( data.get("ProduceValue") ) );		ProduceTpye = data.get("ProduceTpye");		ram = Integer.parseInt( StringUtil.convertNumberString( data.get("ram") ) );		buildtype = Byte.parseByte( StringUtil.convertNumberString( data.get("buildtype") ) );		removedtype = Byte.parseByte( StringUtil.convertNumberString( data.get("removedtype") ) );		mutex = data.get("mutex");		front = data.get("front");		technology = data.get("technology");

	}}