package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class BbuildingPo {	public final int id;	public final String name;	public final String intro;	public final String des;	public final String icon;	public final String model;	public final byte level;	public final String needres;	public final String iconbg;	public final int needtime;	public final byte usegrid;	public final int ProduceValue;	public final String ProduceTpye;	public final int ram;	public final byte removed;	public final int next;	public final String technology;	public final String mutex;

	public BbuildingPo( BbuildingPo clone ){		this.id = clone.id;		this.name = clone.name;		this.intro = clone.intro;		this.des = clone.des;		this.icon = clone.icon;		this.model = clone.model;		this.level = clone.level;		this.needres = clone.needres;		this.iconbg = clone.iconbg;		this.needtime = clone.needtime;		this.usegrid = clone.usegrid;		this.ProduceValue = clone.ProduceValue;		this.ProduceTpye = clone.ProduceTpye;		this.ram = clone.ram;		this.removed = clone.removed;		this.next = clone.next;		this.technology = clone.technology;		this.mutex = clone.mutex;

	}	public BbuildingPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		intro = data.get("intro");		des = data.get("des");		icon = data.get("icon");		model = data.get("model");		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		needres = data.get("needres");		iconbg = data.get("iconbg");		needtime = Integer.parseInt( StringUtil.convertNumberString( data.get("needtime") ) );		usegrid = Byte.parseByte( StringUtil.convertNumberString( data.get("usegrid") ) );		ProduceValue = Integer.parseInt( StringUtil.convertNumberString( data.get("ProduceValue") ) );		ProduceTpye = data.get("ProduceTpye");		ram = Integer.parseInt( StringUtil.convertNumberString( data.get("ram") ) );		removed = Byte.parseByte( StringUtil.convertNumberString( data.get("removed") ) );		next = Integer.parseInt( StringUtil.convertNumberString( data.get("next") ) );		technology = data.get("technology");		mutex = data.get("mutex");

	}}