package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Building {	public final int id;	public final String name;	public final String intro;	public final String des;	public final String icon;	public final String model;	public final String needres;	public final long needmoney;	public final int needtime;	public final short tpye;	public final byte room;	public final int ProduceValue;	public final String ProduceTpye;	public final int ram;

	public Building( Building clone ){		this.id = clone.id;		this.name = clone.name;		this.intro = clone.intro;		this.des = clone.des;		this.icon = clone.icon;		this.model = clone.model;		this.needres = clone.needres;		this.needmoney = clone.needmoney;		this.needtime = clone.needtime;		this.tpye = clone.tpye;		this.room = clone.room;		this.ProduceValue = clone.ProduceValue;		this.ProduceTpye = clone.ProduceTpye;		this.ram = clone.ram;

	}	public Building( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		intro = data.get("intro");		des = data.get("des");		icon = data.get("icon");		model = data.get("model");		needres = data.get("needres");		needmoney = Long.parseLong( StringUtil.convertNumberString( data.get("needmoney") ) );		needtime = Integer.parseInt( StringUtil.convertNumberString( data.get("needtime") ) );		tpye = Short.parseShort( StringUtil.convertNumberString( data.get("tpye") ) );		room = Byte.parseByte( StringUtil.convertNumberString( data.get("room") ) );		ProduceValue = Integer.parseInt( StringUtil.convertNumberString( data.get("ProduceValue") ) );		ProduceTpye = data.get("ProduceTpye");		ram = Integer.parseInt( StringUtil.convertNumberString( data.get("ram") ) );

	}}