package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class CaptainPo {	public final int id;	public final int weekpay;	public final int control;	public final int perception;	public final int affinity;	public final String answer;	public final String asking;	public final String intimate;	public final String race;	public final String age;	public final String character;

	public CaptainPo( CaptainPo clone ){		this.id = clone.id;		this.weekpay = clone.weekpay;		this.control = clone.control;		this.perception = clone.perception;		this.affinity = clone.affinity;		this.answer = clone.answer;		this.asking = clone.asking;		this.intimate = clone.intimate;		this.race = clone.race;		this.age = clone.age;		this.character = clone.character;

	}	public CaptainPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		weekpay = Integer.parseInt( StringUtil.convertNumberString( data.get("weekpay") ) );		control = Integer.parseInt( StringUtil.convertNumberString( data.get("control") ) );		perception = Integer.parseInt( StringUtil.convertNumberString( data.get("perception") ) );		affinity = Integer.parseInt( StringUtil.convertNumberString( data.get("affinity") ) );		answer = data.get("answer");		asking = data.get("asking");		intimate = data.get("intimate");		race = data.get("race");		age = data.get("age");		character = data.get("character");

	}}