package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class ChapterPo {	public final int id;	public final String name;	public final String des;	public final String icon;	public final int time;	public final byte times;	public final int stime;	public final String qc;	public final String qp;	public final String sleeptime;	public final String nquality;	public final int temp;	public final String silverpond;	public final String goldenpond;

	public ChapterPo( ChapterPo clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.icon = clone.icon;		this.time = clone.time;		this.times = clone.times;		this.stime = clone.stime;		this.qc = clone.qc;		this.qp = clone.qp;		this.sleeptime = clone.sleeptime;		this.nquality = clone.nquality;		this.temp = clone.temp;		this.silverpond = clone.silverpond;		this.goldenpond = clone.goldenpond;

	}	public ChapterPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		icon = data.get("icon");		time = Integer.parseInt( StringUtil.convertNumberString( data.get("time") ) );		times = Byte.parseByte( StringUtil.convertNumberString( data.get("times") ) );		stime = Integer.parseInt( StringUtil.convertNumberString( data.get("stime") ) );		qc = data.get("qc");		qp = data.get("qp");		sleeptime = data.get("sleeptime");		nquality = data.get("nquality");		temp = Integer.parseInt( StringUtil.convertNumberString( data.get("temp") ) );		silverpond = data.get("silverpond");		goldenpond = data.get("goldenpond");

	}}