package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Ectype {	public final int id;	public final String name;	public final String des;	public final int money;	public final String reward;	public final String eanswers;	public final byte type;	public final byte times;	public final String etime;	public final byte level;	public final String ranswers;	public final String ianswers;	public final String enemy;	public final int btime;	public final short maxran;

	public Ectype( Ectype clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.money = clone.money;		this.reward = clone.reward;		this.eanswers = clone.eanswers;		this.type = clone.type;		this.times = clone.times;		this.etime = clone.etime;		this.level = clone.level;		this.ranswers = clone.ranswers;		this.ianswers = clone.ianswers;		this.enemy = clone.enemy;		this.btime = clone.btime;		this.maxran = clone.maxran;

	}	public Ectype( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		money = Integer.parseInt( StringUtil.convertNumberString( data.get("money") ) );		reward = data.get("reward");		eanswers = data.get("eanswers");		type = Byte.parseByte( StringUtil.convertNumberString( data.get("type") ) );		times = Byte.parseByte( StringUtil.convertNumberString( data.get("times") ) );		etime = data.get("etime");		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		ranswers = data.get("ranswers");		ianswers = data.get("ianswers");		enemy = data.get("enemy");		btime = Integer.parseInt( StringUtil.convertNumberString( data.get("btime") ) );		maxran = Short.parseShort( StringUtil.convertNumberString( data.get("maxran") ) );

	}}