package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class EctypePo {	public final int id;	public final float power;	public final int minmoney;	public final int maxmoney;	public final int btime;	public final short maxran;	public final String atkvalue;	public final String defvalue;	public final int hp;

	public EctypePo( EctypePo clone ){		this.id = clone.id;		this.power = clone.power;		this.minmoney = clone.minmoney;		this.maxmoney = clone.maxmoney;		this.btime = clone.btime;		this.maxran = clone.maxran;		this.atkvalue = clone.atkvalue;		this.defvalue = clone.defvalue;		this.hp = clone.hp;

	}	public EctypePo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		power = Float.parseFloat( StringUtil.convertNumberString( data.get("power") ) );		minmoney = Integer.parseInt( StringUtil.convertNumberString( data.get("minmoney") ) );		maxmoney = Integer.parseInt( StringUtil.convertNumberString( data.get("maxmoney") ) );		btime = Integer.parseInt( StringUtil.convertNumberString( data.get("btime") ) );		maxran = Short.parseShort( StringUtil.convertNumberString( data.get("maxran") ) );		atkvalue = data.get("atkvalue");		defvalue = data.get("defvalue");		hp = Integer.parseInt( StringUtil.convertNumberString( data.get("hp") ) );

	}}