package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class EnemyPo {	public final int id;	public final String name;	public final String des;	public final String askings;	public final String answers;	public final String atktype;	public final String atkvalue;	public final String deftype;	public final String defvalue;	public final int hp;

	public EnemyPo( EnemyPo clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.askings = clone.askings;		this.answers = clone.answers;		this.atktype = clone.atktype;		this.atkvalue = clone.atkvalue;		this.deftype = clone.deftype;		this.defvalue = clone.defvalue;		this.hp = clone.hp;

	}	public EnemyPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		askings = data.get("askings");		answers = data.get("answers");		atktype = data.get("atktype");		atkvalue = data.get("atkvalue");		deftype = data.get("deftype");		defvalue = data.get("defvalue");		hp = Integer.parseInt( StringUtil.convertNumberString( data.get("hp") ) );

	}}