package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class EnemyPo {	public final int id;	public final String name;	public final String des;	public final byte level;	public final String askings;	public final String answers;	public final String type;	public final String value;	public final int HP;	public final int range;	public final String rewards;

	public EnemyPo( EnemyPo clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.level = clone.level;		this.askings = clone.askings;		this.answers = clone.answers;		this.type = clone.type;		this.value = clone.value;		this.HP = clone.HP;		this.range = clone.range;		this.rewards = clone.rewards;

	}	public EnemyPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		askings = data.get("askings");		answers = data.get("answers");		type = data.get("type");		value = data.get("value");		HP = Integer.parseInt( StringUtil.convertNumberString( data.get("HP") ) );		range = Integer.parseInt( StringUtil.convertNumberString( data.get("range") ) );		rewards = data.get("rewards");

	}}