package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class EnemyPo {	public final int id;	public final String name;	public final String des;	public final byte level;	public final String answer;	public final int lakt;	public final int phakt;	public final int catk;	public final int ldef;	public final int phdef;	public final int cdef;	public final int HP;	public final int range;	public final String rewards;

	public EnemyPo( EnemyPo clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.level = clone.level;		this.answer = clone.answer;		this.lakt = clone.lakt;		this.phakt = clone.phakt;		this.catk = clone.catk;		this.ldef = clone.ldef;		this.phdef = clone.phdef;		this.cdef = clone.cdef;		this.HP = clone.HP;		this.range = clone.range;		this.rewards = clone.rewards;

	}	public EnemyPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		answer = data.get("answer");		lakt = Integer.parseInt( StringUtil.convertNumberString( data.get("lakt") ) );		phakt = Integer.parseInt( StringUtil.convertNumberString( data.get("phakt") ) );		catk = Integer.parseInt( StringUtil.convertNumberString( data.get("catk") ) );		ldef = Integer.parseInt( StringUtil.convertNumberString( data.get("ldef") ) );		phdef = Integer.parseInt( StringUtil.convertNumberString( data.get("phdef") ) );		cdef = Integer.parseInt( StringUtil.convertNumberString( data.get("cdef") ) );		HP = Integer.parseInt( StringUtil.convertNumberString( data.get("HP") ) );		range = Integer.parseInt( StringUtil.convertNumberString( data.get("range") ) );		rewards = data.get("rewards");

	}}