package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Equip_weaponPo {	public final int id;	public final String type;	public final String value;	public final int needen;	public final int precision;	public final int complexity;	public final int surplus;	public final int dur;	public final String answers;	public final String asking;

	public Equip_weaponPo( Equip_weaponPo clone ){		this.id = clone.id;		this.type = clone.type;		this.value = clone.value;		this.needen = clone.needen;		this.precision = clone.precision;		this.complexity = clone.complexity;		this.surplus = clone.surplus;		this.dur = clone.dur;		this.answers = clone.answers;		this.asking = clone.asking;

	}	public Equip_weaponPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		type = data.get("type");		value = data.get("value");		needen = Integer.parseInt( StringUtil.convertNumberString( data.get("needen") ) );		precision = Integer.parseInt( StringUtil.convertNumberString( data.get("precision") ) );		complexity = Integer.parseInt( StringUtil.convertNumberString( data.get("complexity") ) );		surplus = Integer.parseInt( StringUtil.convertNumberString( data.get("surplus") ) );		dur = Integer.parseInt( StringUtil.convertNumberString( data.get("dur") ) );		answers = data.get("answers");		asking = data.get("asking");

	}}