package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Item {	public final int ID;	public final String name;	public final String intro;	public final String des;	public final String icon;	public final String model;	public final byte bagtype;	public final int usegrid;	public final byte usetype;	public final byte outtype;	public final byte defcolor;	public final int buygold;	public final int sellgold;	public final int diamond;	public final byte level;	public final byte maxlevel;	public final long manymax;	public final byte cansm;	public final String[] conditions;	public final byte useminlv;	public final byte usemaxcount;	public final String[] releaseEnd;	public final int attribute;	public final int reply;

	public Item( Map<String, String> data ){		ID = Integer.parseInt( StringUtil.convertNumberString( data.get("ID") ) );		name = data.get("name");		intro = data.get("intro");		des = data.get("des");		icon = data.get("icon");		model = data.get("model");		bagtype = Byte.parseByte( StringUtil.convertNumberString( data.get("bagtype") ) );		usegrid = Integer.parseInt( StringUtil.convertNumberString( data.get("usegrid") ) );		usetype = Byte.parseByte( StringUtil.convertNumberString( data.get("usetype") ) );		outtype = Byte.parseByte( StringUtil.convertNumberString( data.get("outtype") ) );		defcolor = Byte.parseByte( StringUtil.convertNumberString( data.get("defcolor") ) );		buygold = Integer.parseInt( StringUtil.convertNumberString( data.get("buygold") ) );		sellgold = Integer.parseInt( StringUtil.convertNumberString( data.get("sellgold") ) );		diamond = Integer.parseInt( StringUtil.convertNumberString( data.get("diamond") ) );		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		maxlevel = Byte.parseByte( StringUtil.convertNumberString( data.get("maxlevel") ) );		manymax = Long.parseLong( StringUtil.convertNumberString( data.get("manymax") ) );		cansm = Byte.parseByte( StringUtil.convertNumberString( data.get("cansm") ) );		conditions = data.get("conditions").split(";");		useminlv = Byte.parseByte( StringUtil.convertNumberString( data.get("useminlv") ) );		usemaxcount = Byte.parseByte( StringUtil.convertNumberString( data.get("usemaxcount") ) );		releaseEnd = data.get("releaseEnd").split(";");		attribute = Integer.parseInt( StringUtil.convertNumberString( data.get("attribute") ) );		reply = Integer.parseInt( StringUtil.convertNumberString( data.get("reply") ) );

	}}