package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class ItemPo {	public final int id;	public final String name;	public final String intro;	public final String des;	public final String icon;	public final String icon32;	public final String iconclass;	public final String model;	public final String iconbg;	public final byte bagtype;	public final byte itemtype;	public final short usegrid;	public final byte usetype;	public final byte outtype;	public final byte defcolor;	public final int buygold;	public final int diamond;	public final int sellgold;	public final byte maxlevel;	public final byte level;	public final int manymax;	public final byte cansm;	public final String conditions;	public final byte useminlv;	public final byte usemaxcount;	public final String releaseEnd;	public final int attribute;	public final int reply;

	public ItemPo( ItemPo clone ){		this.id = clone.id;		this.name = clone.name;		this.intro = clone.intro;		this.des = clone.des;		this.icon = clone.icon;		this.icon32 = clone.icon32;		this.iconclass = clone.iconclass;		this.model = clone.model;		this.iconbg = clone.iconbg;		this.bagtype = clone.bagtype;		this.itemtype = clone.itemtype;		this.usegrid = clone.usegrid;		this.usetype = clone.usetype;		this.outtype = clone.outtype;		this.defcolor = clone.defcolor;		this.buygold = clone.buygold;		this.diamond = clone.diamond;		this.sellgold = clone.sellgold;		this.maxlevel = clone.maxlevel;		this.level = clone.level;		this.manymax = clone.manymax;		this.cansm = clone.cansm;		this.conditions = clone.conditions;		this.useminlv = clone.useminlv;		this.usemaxcount = clone.usemaxcount;		this.releaseEnd = clone.releaseEnd;		this.attribute = clone.attribute;		this.reply = clone.reply;

	}	public ItemPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		intro = data.get("intro");		des = data.get("des");		icon = data.get("icon");		icon32 = data.get("icon32");		iconclass = data.get("iconclass");		model = data.get("model");		iconbg = data.get("iconbg");		bagtype = Byte.parseByte( StringUtil.convertNumberString( data.get("bagtype") ) );		itemtype = Byte.parseByte( StringUtil.convertNumberString( data.get("itemtype") ) );		usegrid = Short.parseShort( StringUtil.convertNumberString( data.get("usegrid") ) );		usetype = Byte.parseByte( StringUtil.convertNumberString( data.get("usetype") ) );		outtype = Byte.parseByte( StringUtil.convertNumberString( data.get("outtype") ) );		defcolor = Byte.parseByte( StringUtil.convertNumberString( data.get("defcolor") ) );		buygold = Integer.parseInt( StringUtil.convertNumberString( data.get("buygold") ) );		diamond = Integer.parseInt( StringUtil.convertNumberString( data.get("diamond") ) );		sellgold = Integer.parseInt( StringUtil.convertNumberString( data.get("sellgold") ) );		maxlevel = Byte.parseByte( StringUtil.convertNumberString( data.get("maxlevel") ) );		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		manymax = Integer.parseInt( StringUtil.convertNumberString( data.get("manymax") ) );		cansm = Byte.parseByte( StringUtil.convertNumberString( data.get("cansm") ) );		conditions = data.get("conditions");		useminlv = Byte.parseByte( StringUtil.convertNumberString( data.get("useminlv") ) );		usemaxcount = Byte.parseByte( StringUtil.convertNumberString( data.get("usemaxcount") ) );		releaseEnd = data.get("releaseEnd");		attribute = Integer.parseInt( StringUtil.convertNumberString( data.get("attribute") ) );		reply = Integer.parseInt( StringUtil.convertNumberString( data.get("reply") ) );

	}}