package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Tech {	public final int id;	public final String name;	public final String des;	public final byte level;	public final short techtype;	public final byte efftype;	public final byte needlevel;	public final String prevtech;	public final String Mutualtech;	public final String needres;	public final int needmoney;	public final int needtime;	public final String eff;

	public Tech( Tech clone ){		this.id = clone.id;		this.name = clone.name;		this.des = clone.des;		this.level = clone.level;		this.techtype = clone.techtype;		this.efftype = clone.efftype;		this.needlevel = clone.needlevel;		this.prevtech = clone.prevtech;		this.Mutualtech = clone.Mutualtech;		this.needres = clone.needres;		this.needmoney = clone.needmoney;		this.needtime = clone.needtime;		this.eff = clone.eff;

	}	public Tech( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		des = data.get("des");		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		techtype = Short.parseShort( StringUtil.convertNumberString( data.get("techtype") ) );		efftype = Byte.parseByte( StringUtil.convertNumberString( data.get("efftype") ) );		needlevel = Byte.parseByte( StringUtil.convertNumberString( data.get("needlevel") ) );		prevtech = data.get("prevtech");		Mutualtech = data.get("Mutualtech");		needres = data.get("needres");		needmoney = Integer.parseInt( StringUtil.convertNumberString( data.get("needmoney") ) );		needtime = Integer.parseInt( StringUtil.convertNumberString( data.get("needtime") ) );		eff = data.get("eff");

	}}