package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Tech {	public final int id;	public final String name;	public final String desc;	public final byte level;	public final byte type;	public final String pre;	public final String mutex;	public final byte needLevel;	public final String needStuff;	public final int needGold;	public final int needTime;	public final String effect;

	public Tech( Tech clone ){		this.id = clone.id;		this.name = clone.name;		this.desc = clone.desc;		this.level = clone.level;		this.type = clone.type;		this.pre = clone.pre;		this.mutex = clone.mutex;		this.needLevel = clone.needLevel;		this.needStuff = clone.needStuff;		this.needGold = clone.needGold;		this.needTime = clone.needTime;		this.effect = clone.effect;

	}	public Tech( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		desc = data.get("desc");		level = Byte.parseByte( StringUtil.convertNumberString( data.get("level") ) );		type = Byte.parseByte( StringUtil.convertNumberString( data.get("type") ) );		pre = data.get("pre");		mutex = data.get("mutex");		needLevel = Byte.parseByte( StringUtil.convertNumberString( data.get("needLevel") ) );		needStuff = data.get("needStuff");		needGold = Integer.parseInt( StringUtil.convertNumberString( data.get("needGold") ) );		needTime = Integer.parseInt( StringUtil.convertNumberString( data.get("needTime") ) );		effect = data.get("effect");

	}}