package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Weapon {	public final int id;	public final byte type;	public final int room;	public final int en;	public final int precision;	public final int mess;	public final int complexity;	public final int range;	public final int latk;	public final int ldef;	public final int phatk;	public final int phdef;	public final int catk;	public final int cdef;	public final float DPH;	public final int surplus;	public final String answer;

	public Weapon( Weapon clone ){		this.id = clone.id;		this.type = clone.type;		this.room = clone.room;		this.en = clone.en;		this.precision = clone.precision;		this.mess = clone.mess;		this.complexity = clone.complexity;		this.range = clone.range;		this.latk = clone.latk;		this.ldef = clone.ldef;		this.phatk = clone.phatk;		this.phdef = clone.phdef;		this.catk = clone.catk;		this.cdef = clone.cdef;		this.DPH = clone.DPH;		this.surplus = clone.surplus;		this.answer = clone.answer;

	}	public Weapon( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		type = Byte.parseByte( StringUtil.convertNumberString( data.get("type") ) );		room = Integer.parseInt( StringUtil.convertNumberString( data.get("room") ) );		en = Integer.parseInt( StringUtil.convertNumberString( data.get("en") ) );		precision = Integer.parseInt( StringUtil.convertNumberString( data.get("precision") ) );		mess = Integer.parseInt( StringUtil.convertNumberString( data.get("mess") ) );		complexity = Integer.parseInt( StringUtil.convertNumberString( data.get("complexity") ) );		range = Integer.parseInt( StringUtil.convertNumberString( data.get("range") ) );		latk = Integer.parseInt( StringUtil.convertNumberString( data.get("latk") ) );		ldef = Integer.parseInt( StringUtil.convertNumberString( data.get("ldef") ) );		phatk = Integer.parseInt( StringUtil.convertNumberString( data.get("phatk") ) );		phdef = Integer.parseInt( StringUtil.convertNumberString( data.get("phdef") ) );		catk = Integer.parseInt( StringUtil.convertNumberString( data.get("catk") ) );		cdef = Integer.parseInt( StringUtil.convertNumberString( data.get("cdef") ) );		DPH = Float.parseFloat( StringUtil.convertNumberString( data.get("DPH") ) );		surplus = Integer.parseInt( StringUtil.convertNumberString( data.get("surplus") ) );		answer = data.get("answer");

	}}