package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class Weapon {	public final int id;	public final String name;	public final byte tpye;	public final int room;	public final int en;	public final int precision;	public final int mess;	public final int complexity;	public final int range;	public final int latk;	public final int ldef;	public final int phatk;	public final int phdef;	public final int catk;	public final int cdef;	public final float DPH;	public final String answer;	public final int surplus;

	public Weapon( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		name = data.get("name");		tpye = Byte.parseByte( StringUtil.convertNumberString( data.get("tpye") ) );		room = Integer.parseInt( StringUtil.convertNumberString( data.get("room") ) );		en = Integer.parseInt( StringUtil.convertNumberString( data.get("en") ) );		precision = Integer.parseInt( StringUtil.convertNumberString( data.get("precision") ) );		mess = Integer.parseInt( StringUtil.convertNumberString( data.get("mess") ) );		complexity = Integer.parseInt( StringUtil.convertNumberString( data.get("complexity") ) );		range = Integer.parseInt( StringUtil.convertNumberString( data.get("range") ) );		latk = Integer.parseInt( StringUtil.convertNumberString( data.get("latk") ) );		ldef = Integer.parseInt( StringUtil.convertNumberString( data.get("ldef") ) );		phatk = Integer.parseInt( StringUtil.convertNumberString( data.get("phatk") ) );		phdef = Integer.parseInt( StringUtil.convertNumberString( data.get("phdef") ) );		catk = Integer.parseInt( StringUtil.convertNumberString( data.get("catk") ) );		cdef = Integer.parseInt( StringUtil.convertNumberString( data.get("cdef") ) );		DPH = Float.parseFloat( StringUtil.convertNumberString( data.get("DPH") ) );		answer = data.get("answer");		surplus = Integer.parseInt( StringUtil.convertNumberString( data.get("surplus") ) );

	}}