package cn.xgame.config.o;import java.util.Map;import x.javaplus.string.StringUtil;public class WeaponPo {	public final int id;	public final int room;	public final String atktype;	public final String atkvalue;	public final String deftype;	public final String defvalue;	public final int hp;	public final int needen;	public final int mess;	public final int push;	public final int precision;	public final int complexity;	public final int surplus;	public final int dur;	public final String answers;	public final String asking;

	public WeaponPo( WeaponPo clone ){		this.id = clone.id;		this.room = clone.room;		this.atktype = clone.atktype;		this.atkvalue = clone.atkvalue;		this.deftype = clone.deftype;		this.defvalue = clone.defvalue;		this.hp = clone.hp;		this.needen = clone.needen;		this.mess = clone.mess;		this.push = clone.push;		this.precision = clone.precision;		this.complexity = clone.complexity;		this.surplus = clone.surplus;		this.dur = clone.dur;		this.answers = clone.answers;		this.asking = clone.asking;

	}	public WeaponPo( Map<String, String> data ){		id = Integer.parseInt( StringUtil.convertNumberString( data.get("id") ) );		room = Integer.parseInt( StringUtil.convertNumberString( data.get("room") ) );		atktype = data.get("atktype");		atkvalue = data.get("atkvalue");		deftype = data.get("deftype");		defvalue = data.get("defvalue");		hp = Integer.parseInt( StringUtil.convertNumberString( data.get("hp") ) );		needen = Integer.parseInt( StringUtil.convertNumberString( data.get("needen") ) );		mess = Integer.parseInt( StringUtil.convertNumberString( data.get("mess") ) );		push = Integer.parseInt( StringUtil.convertNumberString( data.get("push") ) );		precision = Integer.parseInt( StringUtil.convertNumberString( data.get("precision") ) );		complexity = Integer.parseInt( StringUtil.convertNumberString( data.get("complexity") ) );		surplus = Integer.parseInt( StringUtil.convertNumberString( data.get("surplus") ) );		dur = Integer.parseInt( StringUtil.convertNumberString( data.get("dur") ) );		answers = data.get("answers");		asking = data.get("asking");

	}}